use chrono::NaiveDate;
use nzcp::{verify_pass_barcode, PublicCovidPass};

/// https://nzcp.covid19.health.nz/#valid-worked-example
#[tokio::test]
async fn valid_pass() {
    let barcode = "NZCP:";

    let pass: PublicCovidPass = verify_pass_barcode(barcode).await.unwrap();

    assert_eq!(
        pass,
        PublicCovidPass {
            given_name: String::from("Jack"),
            family_name: Some(String::from("Sparrow")),
            date_of_birth: NaiveDate::from_ymd(1960, 04, 16),
        }
    )
}
